	local iMaxHueID = gHueLoader:GetMaxHueID()
	print("iMaxHueID",iMaxHueID)
	local huelist = {}
	local basehues = {}
	for hue = 0,iMaxHueID-1 do 
		local yr,yg,yb,ya = gHueLoader:GetColor(hue,31)
		local bIsGrey = yr == yg and yg == yb
		if (bIsGrey) then 
			local bAllGrey = true
			for col = 0,31 do 
				local xr,xg,xb,xa = gHueLoader:GetColor(hue,col)
				if (xr == xg and xg == xb) then else bAllGrey = false end
				--~ print("col",hue,col,":",xr,xg,xb,xa)
			end     
			if (bAllGrey) then 
				local palette = {}
				for col = 0,31 do 
					local xr,xg,xb,xa = gHueLoader:GetColor(hue,col)
					palette[col] = xr
				end
				if (palette[31] > 0) then basehues[hue] = palette end
				--~ print("basehue",hue,table.concat(palette,","))  
				--~ print("col",hue,col,bAllGrey,":",yr,yg,yb,ya)  
			end
		---- 1149,2042-2049  
		--- col     2042    nil     :       1       1       1       0 
		--~ col     2057    nil     :       1       1       1       0 
		end
	end
	print("numbases",countarr(basehues))
	
	local tolerance = 0.15 -- the maximum error from the basehue has to be smaller than 30%
	local basehuelist = {}
	for hue = 0,iMaxHueID-1 do 
		if (math.mod(hue,100) == 0) then print("hue",hue) end
		local yr,yg,yb,ya = gHueLoader:GetColor(hue,31)
		local ymax = max(yr,yg,yb)
		local bestbasehue
		local bestmaxerror
		for basehue,palette in pairs(basehues) do
			local basef = ymax / palette[31]
			local fr = max(0,min(1,basef*yr))
			local fg = max(0,min(1,basef*yg))
			local fb = max(0,min(1,basef*yb))
			local maxerror = 0
			for col = 0,31 do 
				local xr,xg,xb,xa = gHueLoader:GetColor(hue,col)
				local curerror = max(	abs(xr - palette[col]*fr),
										abs(xg - palette[col]*fg),
										abs(xb - palette[col]*fb))
				if (curerror > maxerror) then maxerror = curerror end
				--~ print("col",hue,col,":",xr,xg,xb,xa)
			end
			if (maxerror < tolerance and ((not bestmaxerror) or maxerror < bestmaxerror)) then bestmaxerror = maxerror bestbasehue = basehue end
		end
		if (bestbasehue) then
			count_derived = count_derived + 1
			table.insert(basehuelist,"["..hue.."]="..bestbasehue)
		else 
			count_special = count_special + 1
		end
	end
	print("derived:",count_derived,"special:",count_special)
	
	
	local txt = "gBaseHues = { "
	for k,v in pairs(basehuelist) do 
		if (math.mod(k,16) == 0) then txt = txt .. "\n" end
		txt = txt .. v .. ","
	end
		
	print(txt.."}")
	
	os.exit(0)
end
